version.script:
importClass(Packages.org.efaps.admin.datamodel.Type);
importClass(Packages.org.efaps.db.Context);
importClass(Packages.org.efaps.db.Update);

var c = Context.begin(EFAPS_USERNAME);

EFAPS_LOGGER.info("    Update '" + EFAPS_USERNAME + "' Password");
c.getPerson().setPassword(EFAPS_PASSWORD);

EFAPS_LOGGER.info("    Activate Person '" + EFAPS_USERNAME + "'");
var update = new Update(Type.get("Admin_User_Abstract"), "1");
update.add("Status","true");
update.executeWithoutAccessCheck();

EFAPS_LOGGER.info("    Setting default TimeZone, Chronology and Locale for  Person '" + EFAPS_USERNAME + "'");
var update2 = new Update(Type.get("Admin_User_Person"), "1");
update2.add("TimeZone","UTC");
update2.add("Language","1");
update2.add("Chronology","ISO8601");
update2.add("Locale","en_US");
update2.executeWithoutAccessCheck();

EFAPS_LOGGER.info("    Activate Role 'Administration'");
var update = new Update(Type.get("Admin_User_Abstract"), "2");
update.add("Status","true");
update.executeWithoutAccessCheck();

Context.commit();

importClass(Packages.org.efaps.importer.DataImport);
importClass(Packages.java.io.File);
importClass(Packages.org.efaps.db.Context);
jsPackage = Packages.org.mozilla.javascript;
Context.begin(EFAPS_USERNAME);
url = jsPackage.Context.currentContext.currentContext.getClass().getClassLoader().getResource("org/efaps/installations/eFaps-Kernel/Data/IMPORT_Language.xml");
imp = DataImport.readFile(url);
imp.updateInDB();
url = jsPackage.Context.currentContext.currentContext.getClass().getClassLoader().getResource("org/efaps/installations/eFaps-Kernel/Data/IMPORT_PersonToRole.xml");
imp = DataImport.readFile(url);
imp.updateInDB();
url = jsPackage.Context.currentContext.currentContext.getClass().getClassLoader().getResource("org/efaps/installations/eFaps-Kernel/Data/IMPORT_Webapp_RunLevel.xml");
imp = DataImport.readFile(url);
imp.updateInDB();
Context.commit();
